import React, { Component } from 'react';
import '../assets/css/alfa_style.css';
// import '../assets/css/home.css';
import 'bootstrap/dist/css/bootstrap.min.css';
// import '../assets/css/login.css';
// import '../assets/css/mechanics.css';
import '../assets/css/checkout.css';


class CartComponent extends Component {
	constructor() {
		super();
		this.state = {
			accounts2: []
		};
	}

	switchPage(e) {
		window.location = "./checkout.js";
	}

	componentDidMount() {
		fetch('http://localhost:3001/cart/')
			.then(res => res.json())
			.then(data => {
				this.setState(
					(prevState) => { return { accounts2: data.info } }
				)
			});
	}

	getData() {
		fetch('http://localhost:3001/cart/')
			.then(res => res.json())
			.then(data => {
				console.log(data);
				this.setState(
					(prevState) => { return { accounts2: data } }  // I added the .info to data.info
				)
			});
	}

	sum() {
		let sums = 0;

		for (var i = 0; i < this.state.accounts2.length; i++) {
			sums += this.state.accounts2[i].price;
		}

		sums = sums * 100;
		sums = sums / 100;

		return sums;
	}

	delData() {
		for (var i = 0; i < this.state.accounts2.length; i++) {
			let data = { id: this.state.accounts2[i].id, description: this.state.accounts2[i].description, price: this.state.accounts2[i].price };
			let options = {
				method: 'delete',
				headers: {
					'Content-Type': 'application/json'
				},
				body: JSON.stringify(data)
			}
			fetch('http://localhost:3001/checkout/', options);
		}
	}



	render() {
		var mon = this.sum();

		return (
			<div className="main">
				<main className="page payment-page" style={{ marginTop: "-1em"}}>
					<section className="payment-form dark">
						<div className="container">
							<div className="block-heading">
							<h1 align="center" >Shopping Cart</h1>
							</div>
							<form action="./checkout.js" onSubmit={this.switchPage.bind(this)}>
								<div className="products">
									{this.state.accounts2.map((items) => <span key={items.id}>
										<div className="item">
											<span className="price">${items.price}</span>
											<p className="item-name">{items.description}</p>
										</div>
									</span>)}

									<button type="submit" className="btn btn-success btn-lg btn-block">X</button>

									{/* <div className="total">Total<span className="price">${mon}</span></div> */}
								</div>
								<div className="card-details">
									<div className="row">
										<div className="form-group col-sm-12">
											<button type="submit" className="btn btn-success btn-lg btn-block">Proceed to Checkout</button>
										</div>
									</div>
								</div>
							</form>
						</div>
					</section>
				</main>
			</div>
		)
	}
}

